import React, { useRef } from 'react';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

const LOGO_URL = 'https://raw.githubusercontent.com/Giri787/bluwheelz-assets/main/bluwheelz%20logo%20png.png';
const BRAND_COLOR = '#0071BC';

const placeholderVehicles = [
  {
    name: 'TATA Ace EV',
    monthlyCharge: 35000,
    fixedKm: 2000,
    extraKmRate: 12,
    overtimeHr: 150,
    loaderCharges: 2000,
    payload: 900,
    cft: 180,
    refer: false,
    referCharges: 0,
  },
  {
    name: 'Eicher EV',
    monthlyCharge: 65000,
    fixedKm: 3000,
    extraKmRate: 18,
    overtimeHr: 200,
    loaderCharges: 3000,
    payload: 2000,
    cft: 400,
    refer: true,
    referCharges: 15000,
  },
];

const standardRemarks = [
  'EVs have no entry/exit restrictions.',
  'Toll & MCD taxes are additional.',
  'Includes driver, phone, charging, maintenance, and parking.',
  'Deployment area: Within city and as per operational requirement.',
];

export default function QuotationGenerator() {
  const exportRef = useRef();
  const customerName = 'Acme Logistics'; // Placeholder

  // PDF Export
  const handleExportPDF = async () => {
    const input = exportRef.current;
    const canvas = await html2canvas(input, { scale: 2 });
    const imgData = canvas.toDataURL('image/png');
    const pdf = new jsPDF('p', 'pt', 'a4');
    const pdfWidth = pdf.internal.pageSize.getWidth();
    const pdfHeight = pdf.internal.pageSize.getHeight();
    // Calculate image dimensions to fit A4
    const imgProps = pdf.getImageProperties(imgData);
    const ratio = Math.min(pdfWidth / imgProps.width, pdfHeight / imgProps.height);
    const imgWidth = imgProps.width * ratio;
    const imgHeight = imgProps.height * ratio;
    const x = (pdfWidth - imgWidth) / 2;
    const y = 32;
    pdf.addImage(imgData, 'PNG', x, y, imgWidth, imgHeight);
    pdf.save(`Bluwheelz_Quotation.pdf`);
  };

  // PNG Export
  const handleExportPNG = async () => {
    const input = exportRef.current;
    const canvas = await html2canvas(input, { scale: 2 });
    const link = document.createElement('a');
    link.download = 'Bluwheelz_Quotation.png';
    link.href = canvas.toDataURL('image/png');
    link.click();
  };

  return (
    <div className="min-h-screen bg-blue-50 py-8 px-2 flex flex-col items-center">
      {/* Export Buttons */}
      <div className="mb-4 flex gap-2">
        <button
          onClick={handleExportPDF}
          className="bg-[#0071BC] text-white px-4 py-2 rounded-xl shadow text-sm hover:bg-blue-800 transition"
        >
          Export as PDF
        </button>
        <button
          onClick={handleExportPNG}
          className="bg-white border border-blue-200 text-[#0071BC] px-4 py-2 rounded-xl shadow text-sm hover:bg-blue-50 transition"
        >
          Download as PNG
        </button>
      </div>
      {/* Quotation Area (exportable) */}
      <div
        ref={exportRef}
        className="bg-white rounded-xl shadow-lg border border-blue-100 max-w-3xl w-full p-8 relative"
        style={{ fontFamily: 'Inter, sans-serif' }}
      >
        {/* Letterhead */}
        <div className="flex items-center gap-4 mb-6 border-b pb-4 border-blue-100">
          <img src={LOGO_URL} alt="Bluwheelz Logo" className="h-16 w-auto" />
          <div>
            <h1 className="text-2xl font-bold text-[#0071BC] tracking-wide">Quotation</h1>
            <div className="text-sm text-gray-700 font-medium mt-1">{customerName}</div>
          </div>
        </div>
        {/* Company Address */}
        <div className="text-xs text-gray-500 mb-6">
          <div>Bluwheelz Urban Mobility Pvt Ltd</div>
          <div>9th Floor, Building No. 10, Tower B, DLF Cyber City, Phase II,</div>
          <div>Gurugram, Haryana 122002</div>
        </div>
        {/* Table */}
        <div className="overflow-x-auto">
          <table className="w-full text-sm border border-blue-100 rounded-xl overflow-hidden">
            <thead>
              <tr className="bg-blue-50 text-[#0071BC]">
                <th className="py-2 px-3 font-semibold text-left">Parameter</th>
                {placeholderVehicles.map((v, i) => (
                  <th key={i} className="py-2 px-3 font-semibold text-left">{v.name}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {[
                ['Monthly Charge', v => `₹${v.monthlyCharge.toLocaleString()}`],
                ['Fixed KM', v => v.fixedKm],
                ['Extra KM Rate', v => `₹${v.extraKmRate}`],
                ['Overtime/hr', v => `₹${v.overtimeHr}`],
                ['Loader Charges', v => `₹${v.loaderCharges}`],
                ['Payload (kg)', v => v.payload],
                ['Volumetric Space (CFT)', v => v.cft],
                ['0° Refer', v => v.refer ? 'Yes' : 'No'],
              ].map(([label, fn], idx) => (
                <tr key={label} className={idx % 2 === 0 ? 'bg-white' : 'bg-blue-50'}>
                  <td className="py-2 px-3 font-medium text-gray-700 border-b border-blue-100">{label}</td>
                  {placeholderVehicles.map((v, i) => (
                    <td key={i} className="py-2 px-3 border-b border-blue-100">{fn(v)}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        {/* Remarks */}
        <div className="mt-6">
          <div className="font-semibold text-[#0071BC] mb-2">Remarks</div>
          <ul className="list-disc pl-5 text-xs text-gray-700 space-y-1">
            {standardRemarks.map((r, i) => (
              <li key={i}>{r}</li>
            ))}
            {/* Refer charges remark */}
            {placeholderVehicles.filter(v => v.refer && v.referCharges > 0).map((v, i) => (
              <li key={i + 'refer'}>₹{v.referCharges.toLocaleString()} additional for 0° Refer {v.name}</li>
            ))}
          </ul>
        </div>
        {/* Manual Remarks (placeholder) */}
        <div className="mt-4">
          <div className="font-semibold text-[#0071BC] mb-1">Additional Remarks</div>
          <div className="text-xs text-gray-500 italic">(Add your custom remarks here...)</div>
        </div>
      </div>
    </div>
  );
}
